package audit

// AuditSource represents the source/origin of an audit event
type AuditSource string

const (
	// SourceSystem represents audit events generated by internal authsome core
	// This includes events from handlers, core services, and plugins
	SourceSystem AuditSource = "system"

	// SourceApplication represents audit events generated by external API calls
	// This is for events created via the audit API by applications using authsome
	SourceApplication AuditSource = "application"

	// SourcePlugin represents audit events generated by plugins
	// This is a reserved value for future use if needed to distinguish plugin events
	SourcePlugin AuditSource = "plugin"
)

// String returns the string representation of the source
func (s AuditSource) String() string {
	return string(s)
}

// IsValid checks if the source is a valid enum value
func (s AuditSource) IsValid() bool {
	switch s {
	case SourceSystem, SourceApplication, SourcePlugin:
		return true
	default:
		return false
	}
}
